/// <reference types="cypress" />

describe('login', () => {

    it('Deve  realizar cadastro', () => {
        cy.visit('http://localhost:3000/register');
        // cy.get  - busca um elemento
        // type - insere um texto
        cy.get('[placeholder="Digite seu nome"]').type('teste login')
        cy.get('[placeholder="Digite seu email"]').type('emf@teste.com')
        cy.get('[type="password"]').type('123458')

        //routing
        // start server com o cy.server
        //  crir uma rota com o  cy.route
        //atribuir rota com um alias
        //esperar com  cy.wait
        
        cy.intercept('POST','**/dashboard').as('testeintercep')


        cy.get('button').click()

        cy.wait('@testeintercep').then((xhr) =>{
            expect(xhr.status).be.eq(200)
        })
    });

    it('deve realizar login', () => {
        
    });
});